nasal.load:

srand(); # set seed for random generator

var root = cmdarg();
var rootindex = root.getIndex();
var markerPath = "/ai/models/aircraft["~ rootindex ~"]/";

var greenFlash = func()
{
    settimer(func(){ interpolate("/fmee-loop-timer/color-red", 1, 0.6); }, 0.2);
    interpolate("/fmee-loop-timer/color-red", 0.35, 0.2);
}

var markerCoord = geo.Coord.new();
markerCoord.set_latlon(
    getprop(markerPath ~"position/latitude-deg"),
    getprop(markerPath ~"position/longitude-deg"),
    getprop(markerPath ~"position/altitude-ft")
);

var planeCoord = geo.Coord.new();
props.globals.initNode("/fmee-loop-timer/starttime", 0, "DOUBLE");
props.globals.initNode("/fmee-loop-timer/finished-notcleared", 0, "BOOL");
props.globals.initNode("/fmee-loop-timer/start-delay", 1, "BOOL");

var machCheck = maketimer(0.2,
    func()
    {
        if(machCheck == nil) return;

        if(getprop("/velocities/mach") > 1.0 and getprop("/fmee-loop-timer/starttime") > 0)
        {
            screen.log.write("BOOM! You went supersonic! Stopping timed run...");
            print("BOOM! You went supersonic! Stopping timed run...");
            setprop("/fmee-loop-timer/starttime", 0);
            interpolate("/fmee-loop-timer/color-green", 0.35, 2);
        }
        if(getprop("/sim/speed-up") != 1.0 and getprop("/fmee-loop-timer/starttime") > 0)
        {
            screen.log.write("Changing sim speed! You ar cheating! Stopping timed run...");
            print("Changing sim speed! You ar cheating! Stopping timed run...");
            setprop("/fmee-loop-timer/starttime", 0);
            interpolate("/fmee-loop-timer/color-green", 0.35, 2);
        }
    }
);
machCheck.singleShot = 0;

var clearWaypoints = func()
{
    var wayptsNode = props.globals.getNode("/fmee-loop-timer");
    forindex(var i; wayptsNode.getChildren("waypoint"))
    {
        props.globals.initNode("/fmee-loop-timer/waypoint["~ i ~"]/passed", 0, "BOOL");
        setprop("/fmee-loop-timer/waypoint["~ i ~"]/passed", 0);
        #screen.log.write("Clearing waypoint "~ i);
        print("Clearing waypoint "~ i);
    }
}

var distLoop = maketimer(0.1,
    func()
    {
        if(distLoop == nil) return;

        if(getprop("/fmee-loop-timer/starttime") == 0) machCheck.stop();
        if(getprop("/fmee-loop-timer/start-delay"))
        {
            planeCoord.set_latlon(
                getprop("/position/latitude-deg"),
                getprop("/position/longitude-deg"),
                getprop("/position/altitude-ft")
            );
            var dist = markerCoord.direct_distance_to(planeCoord);
            var ubody = getprop("/velocities/uBody-fps");
            var vbody = getprop("/velocities/vBody-fps");
            var wbody = getprop("/velocities/wBody-fps");
            var velocity = 0.3048 * math.sqrt(ubody * ubody + vbody * vbody + wbody * wbody);

            var time1 = dist / velocity / 8;
            var looptime = time1;
            if(time1 < 0.1) looptime = 0.0001;
            if(time1 > 20) looptime = 19 + 2 * rand();

            if(dist < 100)
            {
                setprop("/fmee-loop-timer/starttime", getprop("/sim/time/elapsed-sec"));
                setprop("/sim/multiplay/chat", getprop("/sim/multiplay/callsign") ~" ("~ getprop("/sim/aircraft") ~") started timed FMEE timed loop !");
                machCheck.start();
                clearWaypoints();
                setprop("/fmee-loop-timer/finished-notcleared", 0);
                setprop("/fmee-loop-timer/start-delay", 0);
                settimer(func(){ setprop("/fmee-loop-timer/start-delay", 1); }, 5);
                interpolate("/fmee-loop-timer/color-green", 1, 0.2);
                greenFlash();
            }
            distLoop.restart(looptime);
        }
        else
        {
            distLoop.restart(6);
        }
    }
);
distLoop.singleShot = 1;
var listenStarttime = setlistener("/fmee-loop-timer/starttime", func(){ distLoop.restart(0.2); });
var listenNotcleared = setlistener("/fmee-loop-timer/finished-notcleared", func(){ distLoop.restart(0.2); });
distLoop.start();

nasal.unload:

#screen.log.write("FMEE timed loop start unloaded");
print("FMEE timed loop start unloaded");
removelistener(listenStarttime);
removelistener(listenNotcleared);
distLoop.stop();
distLoop = nil;
machCheck.stop();
machCheck = nil;
